	(6, 1, '2022-07-27 17:57:52', '2022-07-27 17:57:52', '<!-- wp:group {"align":"wide"} -->\n<div class="wp-block-group alignwide"><!-- wp:heading {"textAlign":"center"} -->\n<h2 class="has-text-align-center">The premier destination for modern art in Northern Sweden. Open from 10 AM to 6 PM every day during the summer months.</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {"align":"wide"} -->\n<div class="wp-block-columns alignwide"><!-- wp:column -->\n<div class="wp-block-column"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-1.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>Works and Days</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>August 1 -- December 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">Read More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-3.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>Theatre of Operations</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>October 1 -- December 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">Read More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class="wp-block-column"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-2.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>The Life I Deserve</h3>\n<!-- /wp:heading -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">Read More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-4.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>From Signac to Matisse</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>October 1 -- December 1</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">Read More</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-landscape-2.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {"align":"wide"} -->\n<div class="wp-block-group alignwide"><!-- wp:heading {"textAlign":"center","textColor":"accent"} -->\n<h2 class="has-text-align-center has-accent-color has-text-color">Cyborgs, as the philosopher Donna Haraway established, are not reverent. They do not remember the cosmos.</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph {"dropCap":true} -->\n<p class="has-drop-cap">With seven floors of striking architecture, UMoMA shows exhibitions of international contemporary art, sometimes along with art historical retrospectives. Existential, political and philosophical issues are intrinsic to our programme. As visitor you are invited to guided tours artist talks, lectures, film screenings and other events with free admission</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>The exhibitions are produced by UMoMA in collaboration with artists and museums around the world and they often attract international attention. UMoMA has received a Special Commendation from the European Museum of the Year, and was among the top candidates for the Swedish Museum of the Year Award as well as for the Council of Europe Museum Prize.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {"align":"wide","style":{"color":{"background":"#ffffff"}}} -->\n<div class="wp-block-group alignwide has-background" style="background-color:#ffffff"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:heading {"textAlign":"center"} -->\n<h2 class="has-text-align-center">Become a Member and Get Exclusive Offers!</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {"align":"center"} -->\n<p class="has-text-align-center">Members get access to exclusive exhibits and sales. Our memberships cost $99.99 and are billed annually.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"align":"center"} -->\n<div class="wp-block-button aligncenter"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">Join the Club</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:gallery {"linkTo":"none","align":"wide"} -->\n<figure class="wp-block-gallery alignwide has-nested-images columns-default is-cropped"><!-- wp:image {"id":39,"sizeSlug":"large","linkDestination":"none"} -->\n<figure class="wp-block-image size-large"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-square-2.png" alt="" class="wp-image-39"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {"id":38,"sizeSlug":"large","linkDestination":"none"} -->\n<figure class="wp-block-image size-large"><img src="http://localhost/diazsifontes1/blog/wp-content/themes/twentytwenty/assets/images/2020-square-1.png" alt="" class="wp-image-38"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->', 'The New UMoMA Opens its Doors', '', 'draft', 'closed', 'closed', '', 'the-new-umoma-opens-its-doors-2', '', '', '2022-08-03 21:50:55', '2022-08-03 21:50:55', '', 0, 'http://localhost/diazsifontes1/blog/?page_id=6', 0, 'page', '', 0),
	(7, 1, '2022-07-27 17:57:53', '2022-07-27 17:57:53', '<!-- wp:paragraph -->\n<p>We are a Fast wealthy Family Found in South America</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Diaz Sifontes Family Contains many projects and properties/land not properly managed.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Here, all issues related to Diaz Sifontes are solved. From Family Members to what they Own</p>\n<!-- /wp:paragraph -->', 'About', '', 'publish', 'closed', 'closed', '', 'about', '', '', '2022-08-03 22:36:34', '2022-08-03 22:36:34', '', 0, 'http://localhost/diazsifontes1/blog/?page_id=7', 0, 'page', '', 0),
